import React, { useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { deleteProduct, getProductDetails } from '../actions/productActions'
import Message from '../components/Message'
import { Spinner, Row, Col, Container, Card, Button, Modal } from 'react-bootstrap'
import { Link } from 'react-router-dom'
import { CREATE_PRODUCT_RESET, DELETE_PRODUCT_RESET, UPDATE_PRODUCT_RESET, CARD_CREATE_RESET } from '../constants'


function ProductDetailsPage({ history, match }) {

    const dispatch = useDispatch()

    // modal state and functions
    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    // product details reducer
    const productDetailsReducer = useSelector(state => state.productDetailsReducer)
    const { loading, error, product } = productDetailsReducer

    // login reducer
    const userLoginReducer = useSelector(state => state.userLoginReducer)
    const { userInfo } = userLoginReducer

    // product details reducer
    const deleteProductReducer = useSelector(state => state.deleteProductReducer)
    const { success: productDeletionSuccess } = deleteProductReducer

    useEffect(() => {
        dispatch(getProductDetails(match.params.id))
        dispatch({
            type: UPDATE_PRODUCT_RESET
        })
        dispatch({
            type: CREATE_PRODUCT_RESET
        })
        dispatch({
            type: CARD_CREATE_RESET
        })
    }, [dispatch, match])

    // product delete confirmation
    const confirmDelete = () => {
        dispatch(deleteProduct(match.params.id))
        handleClose()
    }

    // after product deletion
    if (productDeletionSuccess) {
        alert("Product successfully deleted.")
        history.push("/")
        dispatch({
            type: DELETE_PRODUCT_RESET
        })
    }

    return (
        <div>

            {/* Modal Start*/}
            <div>
                <Modal show={show} onHide={handleClose}>
                    <Modal.Header closeButton>
                        <Modal.Title>
                            <i style={{ color: "#e6e600" }} className="fas fa-exclamation-triangle"></i>
                            {" "}
                            Delete Confirmation
                        </Modal.Title>
                    </Modal.Header>
                    <Modal.Body>Are you sure you want to delete this product <em>"{product.productName}"</em>?</Modal.Body>
                    <Modal.Footer>
                        <Button variant="danger" onClick={() => confirmDelete()}>
                            Confirm Delete
                        </Button>
                        <Button variant="primary" onClick={handleClose}>
                            Cancel
                        </Button>
                    </Modal.Footer>
                </Modal>
            </div>

            {/* Modal End */}

            {loading && <span style={{ display: "flex" }}>
                <h5>Getting Product Details</h5>
                <span className="ml-2">
                    <Spinner animation="border" />
                </span>
            </span>}
            {error ? <Message variant='danger'>{error}</Message>
                :
                <div style={{ margin: '0 -1rem 2rem -1rem' }}>
                    <Container>
                        <Row>
                            <Col md={6}>
                                <Card.Img variant="top" src={product.image} height="450" />

                                {/* Product edit and delete conditions */}

                                {userInfo && userInfo.userRole === 1 ?
                                    <span style={{ display: "flex" }}>
                                        <button
                                            className="mt-2 btn btn-primary btn-sm button-focus-css"
                                            onClick={() => history.push(`/product-update/${product.productId}/`)}
                                            style={{ width: "100%" }}
                                        >Chỉnh sửa sản phẩm
                                        </button>

                                        <button
                                            className="ml-2 btn mt-2 btn-danger btn-sm button-focus-css"
                                            style={{ width: "100%" }}
                                            onClick={() => handleShow()}
                                        >Xoá sản phẩm
                                        </button>
                                    </span>
                                    : ""}
                            </Col>

                            <Col sm>
                                <b>{product.productName}</b>
                                <hr />
                                <span className="justify-description-css">
                                    <p>{product.description}</p>
                                </span>
                                <span style={{
                                    display: "flex",
                                    justifyContent: "center",
                                    border: "1px solid",
                                    borderColor: "#C6ACE7",
                                    padding: "2px"
                                }}>
                                    Price:<span className="text-success ml-2">{product.productSalePrice} VND</span>
                                </span>
                            </Col>
                            <Col sm>
                                <b>Mua hàng</b>
                                <hr />
                                {product.stock ?
                                    <Link to={`${product.productId}/checkout/`}>
                                        <button className="btn btn-primary">
                                            <span>Pay with Stripe</span>
                                        </button>
                                    </Link>
                                    :
                                    <Message variant='danger'>
                                        Hết hàng!
                                    </Message>}
                            </Col>
                        </Row>

                    </Container>
                </div>
            }

            <div style={{ margin: '0 0 2rem 0' }}>
                <h5>Mô tả về sản phẩm</h5>
                <p>Mô tả sản phẩm có thể hiểu đơn giản là nội dung tiếp thị, giải thích sản phẩm là gì và tại sao sản phẩm đó đáng mua. Mục đích của mô tả sản phẩm là cung cấp cho khách hàng chi tiết về các tính năng và lợi ích của sản phẩm để từ đó khiến khách hàng muốn mua hàng.
                    Nhiều người bán hàng thường mắc một sai lầm kinh điển. Đó là hay xem nhẹ và bỏ qua việc viết mô tả sản phẩm hay chỉ viết một cách qua loa, đại khái cho có. Trong khi đây là công đoạn rất quan trọng trong quá trình bán hàng. Thay đổi ngay đi nếu như bạn muốn doanh số bán hàng của bạn tăng trưởng. Hãy tham khảo 9 mẹo viết mô tả sản phẩm đơn giản mà hiệu quả để giúp bạn thu hút khách hàng ngay từ dòng đầu tiên.</p>
            </div>

            <div>
                <h5>Các sản phẩm khác dành cho bạn</h5>
            </div>
        </div>

    )
}

export default ProductDetailsPage
