import React, { useState, useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { Form, Button } from 'react-bootstrap'
import { createProduct } from '../actions/productActions'
import { useHistory } from 'react-router'
import { checkTokenValidation, logout } from '../actions/userActions'
import { CREATE_PRODUCT_RESET } from '../constants'
import Message from '../components/Message';
import axios from 'axios'
import api from '../Config/ConfigApi'


const ProductCreatePage = () => {

    let history = useHistory()
    const dispatch = useDispatch()

    const [name, setName] = useState("")
    const [description, setDescription] = useState("")
    const [price, setPrice] = useState("")
    const [priceDiscount, setPriceDiscount] = useState("")
    const [stock, setStock] = useState("")
    const [image, setImage] = useState(null)
    const [category, setcategory] = useState(null)

    // login reducer
    const userLoginReducer = useSelector(state => state.userLoginReducer)
    const { userInfo } = userLoginReducer

    // create product reducer
    const createProductReducer = useSelector(state => state.createProductReducer)
    const { product, success: productCreationSuccess, error: productCreationError } = createProductReducer

    // check token validation reducer
    const checkTokenValidationReducer = useSelector(state => state.checkTokenValidationReducer)
    const { error: tokenError } = checkTokenValidationReducer

    useEffect(() => {
        if (!userInfo) {
            history.push("/login")
        }
        dispatch(checkTokenValidation())
        LoadCategory()
    }, [dispatch, userInfo, history])

    const LoadCategory = () => {
        const { data } = axios.get(
            `${api}Category`
        )

        setcategory(data)
    }

    const onSubmit = (e) => {
        e.preventDefault()

        let form_data = {
            'ProductName': name,
            'ProductDescription': description,
            'ProductCost': price,
            'ProductStock': stock,
            'UserId': userInfo.userId
        }
        dispatch(createProduct(form_data))
    }

    if (productCreationSuccess) {
        alert("Product successfully created.")
        history.push(`/product/${product.id}/`)
        dispatch({
            type: CREATE_PRODUCT_RESET
        })
    }

    if (userInfo && tokenError === "Request failed with status code 401") {
        alert("Session expired, please login again.")
        dispatch(logout())
        history.push("/login")
        window.location.reload()
    }

    return (
        <div style={{
            width: '100%',
            backgroundColor: 'white',
            borderRadius: '20px',
            height: '65vh',
            justifyContent: 'center',
            padding: '2rem',
            boxShadow: 'rgba(50, 50, 93, 0.25) 0px 6px 12px -2px, rgba(0, 0, 0, 0.3) 0px 3px 7px -3px'
        }}>
            {/* {productCreationError && <Message variant='danger'>{productCreationError.image[0]}</Message>} */}
            <h3 style={{
                textAlign: 'center'
            }}>Thêm sản phẩm mới</h3>
            <Form onSubmit={onSubmit}>

                <Form.Group controlId='name'>
                    <Form.Label>
                        <b>
                            Tên sản phẩm
                        </b>
                    </Form.Label>
                    <Form.Control
                        required
                        autoFocus={true}
                        type="text"
                        value={name}
                        placeholder="Nhập tên sản phẩm"
                        onChange={(e) => setName(e.target.value)}
                    >
                    </Form.Control>
                </Form.Group>

                <Form.Group controlId='description'>
                    <Form.Label>
                        <b>
                            Mô tả sản phẩm
                        </b>
                    </Form.Label>
                    <Form.Control
                        required
                        type="text"
                        value={description}
                        placeholder="Nhập mô tả sản phẩm"
                        onChange={(e) => setDescription(e.target.value)}
                    >
                    </Form.Control>
                </Form.Group>

                <Form.Group controlId='price'>
                    <Form.Label>
                        <b>
                            Giá
                        </b>
                    </Form.Label>
                    <Form.Control
                        required
                        type="text"
                        pattern="[0-9]+(\.[0-9]{1,2})?%?"
                        value={price}
                        placeholder="Nhập giá VND"
                        step="0.01"
                        onChange={(e) => setPrice(e.target.value)}
                    >
                    </Form.Control>
                </Form.Group>

                <Form.Group controlId='priceDiscount'>
                    <Form.Label>
                        <b>
                            Giảm giá
                        </b>
                    </Form.Label>
                    <Form.Control
                        required
                        type="text"
                        pattern="[0-9]+(\.[0-9]{1,2})?%?"
                        value={price}
                        placeholder="Nhập giá đã giảm VND"
                        step="0.01"
                        onChange={(e) => setPriceDiscount(e.target.value)}
                    >
                    </Form.Control>
                </Form.Group>

                <Form.Group controlId="category">
                    <Form.Label>
                        <b>Loại mặt hàng</b>
                    </Form.Label>
                    <Form.Control
                        as="select"
                        value={category}
                        onChange={(e) => setcategory(e.target.value)}
                    >
                        <option value="">Chọn loại mặt hàng</option>
                        <option value="100000">100,000 VND</option>
                        <option value="200000">200,000 VND</option>
                        <option value="300000">300,000 VND</option>
                        <option value="400000">400,000 VND</option>
                        <option value="500000">500,000 VND</option>
                    </Form.Control>
                </Form.Group>

                <Form.Group controlId='date'>
                    <Form.Label>
                        <b>
                            Hạn sử dụng
                        </b>
                    </Form.Label>
                    <Form.Control
                        required
                        type="Date"
                        value={price}
                        placeholder="Nhập giá đã giảm VND"
                        step="0.01"
                        onChange={(e) => setPriceDiscount(e.target.value)}
                    >
                    </Form.Control>
                </Form.Group>


                <button type="submit" class="btn btn-success mr-4"
                    style={{
                        borderRadius: '.5rem',
                        padding: '.5rem 2rem'
                    }}>
                    Thêm sản phẩm
                </button>
                <button type="submit" class="btn btn-danger"
                    style={{
                        borderRadius: '.5rem',
                        padding: '.5rem 2rem'
                    }}
                    onClick={() => history.push("/")}
                >
                    Huỷ
                </button>
            </Form>
        </div>
    )
}

export default ProductCreatePage
